-- Données de test pour les distributions
USE gestion_distribution;

-- Signatures de test
INSERT INTO signatures (household_id, recipient_id, signature_data, type_signature, ip_address, device_info, qr_token)
SELECT 
    h.id,
    r.id,
    'data:image/png;base64,',
    'electronique',
    '127.0.0.1',
    'Test Device',
    CONCAT('QR-', h.token_number)
FROM households h
JOIN recipients r ON h.id = r.household_id
LIMIT 3;

-- Distributions de test
INSERT INTO distributions (household_id, recipient_id, signature_id, distribution_date, status, notes, created_by)
SELECT 
    h.id,
    r.id,
    s.id,
    DATE_SUB(NOW(), INTERVAL RAND() * 30 DAY),
    'completed',
    'Distribution de test',
    'Admin'
FROM households h
JOIN recipients r ON h.id = r.household_id
JOIN signatures s ON h.id = s.household_id AND r.id = s.recipient_id;

-- Articles distribués
INSERT INTO distribution_items (id, distribution_id, item_id, quantite, notes)
SELECT 
    UUID(),
    d.id,
    i.id,
    FLOOR(RAND() * 5) + 1,
    'Article de test'
FROM distributions d
CROSS JOIN items i
WHERE i.id <= 3
LIMIT 10;

-- Message de confirmation
SELECT 'Données de test pour les distributions ajoutées avec succès.' AS message;
